using System;using System.Collections.Generic;using System.Text;namespace ConsoleApp2{    public class Bag    {        public List<Consignment> Deliveries { get; } = new List<Consignment>();        public List<IValuable> Valuables { get; } = new List<IValuable>();        public void Add(Consignment item)        {            Deliveries.Add(item);            if (item is IValuable)            {                Valuables.Add((item as IValuable));            }        }        public void Add(PostalOrder item) => Valuables.Add((IValuable)item);
    }    class Program    {        static void Main(string[] args)        {            Bag bag = new Bag();
            // Postcard Letter Package ValuableLetter ValuablePackage //            Console.WriteLine("Zásilky");            foreach (var item in bag.Deliveries)            {                Console.WriteLine(item);            }            Console.WriteLine("Cennosti");            foreach (var item in bag.Valuables)            {                Console.WriteLine(item);            }        }    }   }